import { Component } from '@angular/core';
import { POKEMON } from 'src/app/mocks/pokemon.mocks';
@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {
overMouse:boolean=false;
pokemons=  POKEMON;
imgPokebolaAbierta:string="../../../assets/images/pokebolaAbierta.png"


cambiarImg( id:number){


}

}
<footer class="footer">
  <div class="footer__container-footer">

      <div class="footer__title-footer">
          <h3>OTHERS</h3>
      </div>

      <div class="footer__footer-buttoms">
        <div class="footer__footer-buttomspokemon" *ngFor="let pokemon of pokemons" >
          <app-pokemon-others [img]="pokemon.img" (mouseover)="cambiarImg(pokemon.id)" ></app-pokemon-others>
        </div>


      </div>
  </div>




</footer>
